import React, { useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

function App() {
  const [sessionId, setSessionId] = useState('');
  const [gameId, setGameId] = useState('');

  const handleCreateSession = () => {
    socket.emit('create session');
    socket.on('session created', (data) => {
      setSessionId(data.session_id);
      alert(`Session created! Share this ID with your friends: ${data.session_id}`);
    });
  };

  const handleJoinSession = () => {
    if (!gameId) {
      alert('Please enter a valid Game ID');
      return;
    }

    socket.emit('join session', gameId);
    socket.on('error', (data) => {
      alert(data.message);
    });

    socket.on('session joined', (data) => {
      alert(`Joined session: ${data.session_id}`);
    });

    socket.on('start game', () => {
      alert('The game is starting!');
    });
  };

  return (
      <div className="App">
        <button onClick={handleCreateSession}>Create new session</button>
        {sessionId && <p>Session ID: {sessionId}</p>}

        <input
            type="text"
            placeholder="Game ID"
            value={gameId}
            onChange={(e) => setGameId(e.target.value)}
        />
        <button onClick={handleJoinSession}>Join Game</button>
      </div>
  );
}

export default App;
