import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

function App() {
  const [sessionId, setSessionId] = useState('');
  const [roomId, setRoomId] = useState('');
  const [inGame, setInGame] = useState(false); // новое состояние
  const [card, setCard] = useState(Card);
  useEffect(() => {
    // Подписка на события сокета
    socket.on('session created', (data) => {
      setSessionId(data.gameId);
      setInGame(true); // После создания игры убираем кнопки
      alert(`Session created! Share this ID with your friends: ${data.gameId}`);
    });

    socket.on('session joined', (data, card) => {
      setInGame(true); // После присоединения тоже убираем кнопки
      setCard(card);
      alert(`Joined session: ${data.gameId}`);
    });

    socket.on('start game', () => {
      alert('The game is starting!');
    });

    socket.on('error', (data) => {
      alert(data.message);
    });

    return () => {

      socket.off('session created');
      socket.off('session joined');
      socket.off('start game');
      socket.off('error');
    };
  }, []);

  const handleCreateSession = () => {
    socket.emit('create session');
  };

  const handleJoinSession = () => {
    if (!roomId) {
      alert('Please enter a valid Game ID');
      return;
    }

    socket.emit('join session', roomId);
  };

  return (
      <div className="App">
        {!inGame && (
        <header className="App-header">
          <h1>Furnace</h1>
        </header>
        )}

        <main className="App-main">
          {!inGame && ( // Показываем только если игрок еще не в комнате
              <>
                <p>Create new Game</p>
                <button onClick={handleCreateSession}>New Game</button>

                {sessionId && <p>Session ID: {sessionId}</p>}

                <p>Or Enter Game ID to join:</p>
                <div className="input-container">
                  <input
                      id="input-game-id"
                      type="text"
                      placeholder="Game ID"
                      value={roomId}
                      onChange={(e) => setRoomId(e.target.value)}
                  />
                  <button onClick={handleJoinSession}>Join Game</button>
                </div>
              </>
          )}

          {inGame && <p id="game-id-created">Game ID: {sessionId || roomId} {Card(card)}</p>}
        </main>
      </div>
  );
}


const renderProductionRow = (side) => {
  if (side.type === "exchange") {
    return (
        <>
          {side.details.from.map((resource, idx) => (
              <div key={idx} className={`resource ${resource}`}></div>
          ))}
          <div className="conversion">
            <span className="usage">{side.maxUses}</span>
            <span className="arrow">→</span>
          </div>
          {side.details.to.map((resource, idx) => ( //  ACHTUNG!!!!! тут надо коины обрабатывать
              <div key={idx} className={`resource ${resource}`}></div>
          ))}
        </>
    );
  }
}

const Card = ({card}) => {
  return (
      <div className="card">
        <div className="compensation">
          {card.compensation.type === "production" ? (
              card.compensation.details.what.map((resource, idx) => (
                  <div key={idx} className={`resource ${resource}`}></div>
              ))
          ) : (
              <>
                  {card.compensation.details.from.map((resource, idx) => (
                      <div key={idx} className={`resource ${resource}`}></div>
                  ))}
                    <span className="arrow"></span>
                    {card.compensation.details.to.map((resource, idx) => (
                        <div key={idx} className={`resource ${resource}`}></div>
                    ))}
              </>
          )}
        </div>
        <div className="image-placeholder">
          <p>Здесь может быть картинка производственного здания</p>
        </div>
        <div className="production">
          <div className={"production-row"}>
            {renderProductionRow(card.firstSide)}
          </div>
          <div className={"production-row transparent"}>
            {renderProductionRow(card.secondSide)}
          </div>
        </div>
      </div>
  );
}

export default App;
