import "./App.css"

const renderProductionRow = (side) => {
  if (side.type === "production") {
    return (
        <>
          {side.details.what.map((resource, idx) => (
              <div key={idx} className={`resource ${resource}`}></div>
          ))}
        </>
    );
  }

  // Если тип "exchange"
  return (
      <>
        {side.details.from.map((resource, idx) => (
            <div key={idx} className={`resource ${resource}`}></div>
        ))}

        <div className="conversion">
          <span className="usage">x{side.maxUses}</span>
          <span className="arrow">→</span>
        </div>

        {side.details.to.map((resource, idx) => {
          if (resource === "coin" && typeof side.details.to[idx + 1] === 'number') {
            return (
                <>
                  <div className="resource coin"></div>
                  <span className="usage">x{side.details.to[idx + 1]}</span>
                </>
            );
          }
          if (typeof resource === 'number') return null; // Пропускаем числа
          return <div key={idx} className={`resource ${resource}`}></div>;
        })}
      </>
  );
};

const Card = ({ card }) => {
  const renderCompensation = (compensation) => {
    const { type, details } = compensation;

    if (type === "production") {
      return details.what.map((resource, idx) => (
          <div key={idx} className={`resource ${resource}`}></div>
      ));
    }

    // Если компенсация - это "exchange"
    return (
        <>
          {details.from.map((resource, idx) => (
              <div key={idx} className={`resource ${resource}`}></div>
          ))}
          <span className="arrow">→</span>
          {details.to.map((resource, idx) => (
              <div key={idx} className={`resource ${resource}`}></div>
          ))}
        </>
    );
  };

  return (
      <div className="card">
        <div className="compensation">
          {renderCompensation(card.compensation)}
        </div>

        <div className="image-placeholder">
          <p>Здесь может быть картинка производственного здания</p>
