// client/src/components/GameScreen.js
import React from 'react';
import { useState, useEffect } from 'react';
import '../App.css';
import {CardsGrid} from './CardsAuctionRender'

function GameScreen({ sessionId }) {
    const [playerCards, setPlayerCards] = useState([]);

    useEffect(() => {
        // Подписываемся на обновление карт
        socket.on('update player cards', (cards) => {
            setPlayerCards(cards);  // Обновляем состояние с картами игрока
        });

        return () => {
            socket.off('update player cards');
        };
    }, []);

    return (
        <>
            <p id="game-id-created">Game ID: {sessionId}</p>
            {/* Тут можно добавить игровую логику и интерфейс */}
            <CardsGrid/>
        </>
    );
}

export default GameScreen;
