import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import RoomScreen from '../src/сomponents/RoomScreen';
import GameScreen from '../src/сomponents/GameScreen';

const socket = io('http://localhost:3001');

function App() {
  const [sessionId, setSessionId] = useState('');
  const [roomId, setRoomId] = useState('');
  const [inGame, setInGame] = useState(false);

  useEffect(() => {
    socket.on('session created', (data) => {
      setSessionId(data.gameId);
      setInGame(true);
      alert(`Session created! Share this ID with your friends: ${data.gameId}`);
    });

    socket.on('session joined', (data) => {
      setInGame(true);
      setSessionId(data.gameId);
      alert(`Joined session: ${data.gameId}`);
    });

    socket.on('start game', () => {
      alert('The game is starting!');
    });

    socket.on('error', (data) => {
      alert(data.message);
    });

    return () => {
      socket.off('session created');
      socket.off('session joined');
      socket.off('start game');
      socket.off('error');
    };
  }, []);

  return (
      <div className="App">
        {!inGame && (
            <header className="App-header">
              <h1>Furnace</h1>
            </header>
        )}

        <main className="App-main">
          {!inGame && ( // Показываем только если игрок еще не в комнате
              <>
                <p>Create new Game</p>
                <button onClick={handleCreateSession}>New Game</button>

                {sessionId && <p>Session ID: {sessionId}</p>}

                <p>Or Enter Game ID to join:</p>
                <div className="input-container">
                  <input
                      id="input-game-id"
                      type="text"
                      placeholder="Game ID"
                      value={roomId}
                      onChange={(e) => setRoomId(e.target.value)}
                  />
                  <button onClick={handleJoinSession}>Join Game</button>
                </div>
              </>
          )}

          {inGame && <p id="game-id-created">Game ID: {sessionId || roomId} {/*Card(card.findOne())*/}</p>}
        </main>
      </div>
  );
}

